import React, { Component } from 'react';

import { Row, Col, Typography, Button, Popconfirm } from 'antd';

const styles = {
  text: {
    fontFamily: 'Avenir',
    fontSize: 18,
    width: '100%'
  },
  button: {
    color: '#fff',
    backgroundColor: 'red',
    borderColor: 'red',
    marginTop: 10
  },
  container: {
    borderColor: '#ffae19',
    borderWidth: 1,
    borderRadius: 10
  }
}


export default class ListPanel extends Component {
  render() {
    const { text, onClick } = this.props;
    const confirm = "Are you sure you would like to delete this item?";
    return(
      <div style={styles.container}>
        <Row>
          <Col span={20}>
            <Typography style={styles.text}>{text}</Typography>
          </Col>
          <Col span={4} type="flex" align="end">
            <Popconfirm placement="right" title={confirm} onConfirm={onClick} okText="Yes" cancelText="No">
              <Button
                type="primary"
                shape="circle"
                icon="close"
                size="small"
                style={styles.button} />
            </Popconfirm>
          </Col>
        </Row>
      </div>
    );
  }
};
